mapper.select:
SELECT *
FROM actormember
WHERE tenantid = #{tenantid}
AND id = #{id}

SELECT COUNT(*)
FROM actormember
WHERE tenantid = #{tenantid}
AND actorId = #{actorId}

SELECT COUNT(user_.*)
FROM user_, actormember
WHERE user_.tenantid = #{tenantid}
AND user_.tenantid = actormember.tenantid
AND actormember.actorId = #{actorId}
AND user_.id = actormember.userId

SELECT COUNT(role.*)
FROM role, actormember
WHERE role.tenantid = #{tenantid}
AND role.tenantid = actormember.tenantid
AND actormember.actorId = #{actorId}
AND role.id = actormember.roleId

SELECT COUNT(group_.*)
FROM group_, actormember
WHERE group_.tenantid = #{tenantid}
AND group_.tenantid = actormember.tenantid
AND actormember.actorId = #{actorId}
AND group_.id = actormember.groupId

SELECT *
FROM actormember
WHERE tenantid = #{tenantid}
AND actorId = #{actorId}

	ORDER BY ${orderBy}

SELECT *
FROM actormember
WHERE tenantid = #{tenantid}

	ORDER BY ${orderBy}

SELECT *
FROM actormember
WHERE tenantid = #{tenantid}
AND userId = #{userId}

	ORDER BY ${orderBy}

SELECT *
FROM actormember
WHERE tenantid = #{tenantid}
AND groupId = #{groupId}

	ORDER BY ${orderBy}

SELECT *
FROM actormember
WHERE tenantid = #{tenantid}
AND roleId = #{roleId}

	ORDER BY ${orderBy}

SELECT *
FROM actormember
WHERE tenantid = #{tenantid}
AND roleId = #{roleId}
AND groupId = #{groupId}

	ORDER BY ${orderBy}

mapper.delete:
DELETE FROM actormember
WHERE tenantid = #{tenantid}
AND id = #{id}
