mapper.update:
UPDATE category

	name=#{name},
	description=#{description},
	lastUpdateDate=#{lastUpdateDate},

WHERE tenantid = #{tenantid}
AND id = #{id}

mapper.delete:
DELETE FROM category
WHERE tenantid = #{tenantid}
AND id = #{id}

mapper.select:
SELECT *
FROM category category
WHERE category.tenantid = #{tenantid}
AND category.id = #{id}

	ORDER BY ${orderBy}

SELECT *
FROM category category
WHERE category.tenantid = #{tenantid}
AND name = #{name}

	ORDER BY ${orderBy}

SELECT COUNT(*)
FROM category category
WHERE category.tenantid = #{tenantid}

	ORDER BY ${orderBy}

SELECT *
FROM category category
WHERE category.tenantid = #{tenantid}

	ORDER BY ${orderBy}

SELECT category.*
FROM category, processcategorymapping
WHERE category.tenantid = #{tenantid}
AND category.tenantid = processcategorymapping.tenantid
AND category.id = processcategorymapping.categoryId
AND processcategorymapping.processId = #{processId}

	ORDER BY ${orderBy}
