mapper.delete:
DELETE FROM migration_plan
WHERE tenantid = #{tenantid}
AND id = #{id}

mapper.select:
SELECT *
FROM migration_plan mp
WHERE mp.tenantid = #{tenantid}
AND mp.id = #{id}

