mapper.update:
	UPDATE process_instance_token_count

      processDefinitionId=#{processDefinitionId},

    WHERE tenantid = #{tenantid}
    AND id = #{id}

mapper.delete:
DELETE FROM process_instance_token_count
WHERE tenantid = #{tenantid}
AND id = #{id}

mapper.select:
SELECT COUNT(pitc.id)
FROM process_instance_token_count pitc
WHERE pitc.tenantid = #{tenantid}
AND pitc.processInstanceId = #{processInstanceId}

	AND ${filter}

SELECT pitc.*
FROM process_instance_token_count pitc
WHERE pitc.tenantid = #{tenantid}
AND pitc.processInstanceId = #{processInstanceId}

	AND ${filter}


	ORDER BY ${orderBy}

SELECT COUNT(pitc.id)
FROM process_instance_token_count pitc
WHERE pitc.tenantid = #{tenantid}

	AND ${filter}

SELECT pitc.*
FROM process_instance_token_count pitc
WHERE pitc.tenantid = #{tenantid}

	AND ${filter}


	ORDER BY ${orderBy}

SELECT pitc.*
FROM process_instance_token_count pitc
WHERE pitc.tenantid = #{tenantid}
AND pitc.id = #{id}

	AND ${filter}


	ORDER BY ${orderBy}
