mapper.delete:
DELETE FROM hidden_activity
WHERE tenantid = #{tenantid}
AND id = #{id}

mapper.select:
SELECT h.*
FROM hidden_activity h
WHERE tenantid = #{tenantid}
AND h.id = #{id}

SELECT h.*
FROM hidden_activity h
WHERE tenantid = #{tenantid}
AND h.activityId = #{activityInstanceId}
AND h.userId = #{userId}

SELECT h.*
FROM hidden_activity h
WHERE tenantid = #{tenantid}
AND h.activityId = #{activityInstanceId}

SELECT COUNT(h.id)
FROM hidden_activity h
WHERE tenantid = #{tenantid}
AND h.activityId = #{activityId}
AND h.userId = #{userId}
