mapper.update:
UPDATE process_instance
processDefinitionId=#{processDefinitionId}, description=NULL, description=#{description}, tokenCount=#{tokenCount}, stateId=#{stateId}, stateCategory=#{stateCategory}, startDate=#{startDate}, endDate=#{endDate}, lastUpdate=#{lastUpdate}, containerId=#{containerId}, migration_plan=#{migrationPlanId}, stringIndex1=NULL, stringIndex1=#{stringIndex1}, stringIndex2=NULL, stringIndex2=#{stringIndex2}, stringIndex3=NULL, stringIndex3=#{stringIndex3}, stringIndex4=NULL, stringIndex4=#{stringIndex4}, stringIndex5=NULL, stringIndex5=#{stringIndex5},
WHERE tenantid = #{tenantid} AND id = #{id}

mapper.delete:
DELETE FROM process_instance
WHERE tenantid = #{tenantid}
AND id = #{id}

mapper.select:
SELECT p.*
FROM process_instance p
WHERE p.tenantid = #{tenantid}

	ORDER BY ${orderBy}

SELECT p.*
FROM process_instance p
WHERE p.tenantid = #{tenantid}
AND p.id = #{id}

SELECT p.*
FROM process_instance p
WHERE p.tenantid = #{tenantid}
AND p.callerId = #{callerId}

SELECT p.*
FROM process_instance p
WHERE p.tenantid = #{tenantid}
AND p.processDefinitionId = #{processDefinitionId}

	ORDER BY ${orderBy}

SELECT p.id
FROM process_instance p
WHERE p.tenantid = #{tenantid}
AND p.containerId = #{containerId}

	ORDER BY ${orderBy}

SELECT COUNT(*)
FROM process_instance p
WHERE p.tenantid = #{tenantid}

SELECT COUNT(*)
FROM process_instance p
WHERE p.tenantid = #{tenantid}
AND p.containerId = #{containerId}

SELECT COUNT(DISTINCT p.id)
FROM process_instance p
WHERE p.stateId != 6
AND p.tenantid = #{tenantid}

	AND ${filter}

SELECT p.*
FROM process_instance p
WHERE p.tenantid = #{tenantid}
AND p.stateId != 6

	AND ${filter}


	ORDER BY ${orderBy}

SELECT COUNT( distinct p.id)
FROM process_instance p,
	 processsupervisor supervisor
WHERE p.tenantid = #{tenantid}
AND p.tenantid = supervisor.tenantid
AND p.stateId != 6
AND p.processDefinitionId IN (
	SELECT ps.processDefId
	FROM processsupervisor ps
	WHERE ps.tenantid = p.tenantid
	AND (
		ps.userId = #{userId}
		OR (
			ps.id IN (
				SELECT s.id
				FROM processsupervisor s,
					 user_membership um
				WHERE s.tenantid = p.tenantid
				AND um.tenantid = s.tenantid
				AND um.userId = #{userId} 
				AND (
					(s.groupId = um.groupId AND s.roleId <= 0)
					OR (s.roleId = um.roleId AND s.groupId <= 0)
					OR (s.groupId = um.groupId AND s.roleId = um.roleId)
				)
			)
		)
	)
)

	AND ${filter}

SELECT distinct p.*
FROM process_instance p,
	 processsupervisor supervisor
WHERE p.tenantid = #{tenantid}
AND p.tenantid = supervisor.tenantid
AND p.stateId != 6
AND p.processDefinitionId IN (
	SELECT ps.processDefId
	FROM processsupervisor ps
	WHERE ps.tenantid = p.tenantid
	AND (
		ps.userId = #{userId}
		OR (
			ps.id IN (
				SELECT s.id
				FROM processsupervisor s,
					 user_membership um
				WHERE s.tenantid = p.tenantid
				AND um.tenantid = s.tenantid
				AND um.userId = #{userId} 
				AND (
					(s.groupId = um.groupId AND s.roleId <= 0)
					OR (s.roleId = um.roleId AND s.groupId <= 0)
					OR (s.groupId = um.groupId AND s.roleId = um.roleId)
				)
			)
		)
	)
)

	AND ${filter}


	ORDER BY ${orderBy}

SELECT DISTINCT p.*
FROM process_instance p,
	 flownode_instance f,
	 arch_flownode_instance af
WHERE p.tenantid = #{tenantid}
AND f.tenantid = p.tenantid
AND af.tenantid = p.tenantid
AND p.stateId != 6
AND (
	(
		f.logicalGroup2 = p.id
	  	AND f.assigneeId = #{userId}
	)
	OR (
		af.logicalGroup2 = p.id
		AND af.assigneeId = #{userId}
	)
	OR (
		f.logicalGroup2 = p.id
		AND f.stateId = 4
		AND f.actorId IN (
			SELECT actor.id
			FROM actor actor,
				 actormember actormember
			WHERE actor.tenantid = p.tenantid
			AND actormember.tenantid = actor.tenantid
			AND actor.id = actormember.actorId 
			AND (
				actormember.userId = #{userId}
				OR actormember.id IN (
					SELECT actormember.id
					FROM actormember am,
						 user_membership um
					WHERE am.tenantid = p.tenantid
					AND am.tenantid = um.tenantid
					AND um.userId = #{userId}
					AND (
						(am.groupId = um.groupId AND am.roleId <= 0)
						OR (am.roleId = um.roleId AND am.groupId <= 0)
						OR (am.groupId = um.groupId AND am.roleId = um.roleId)
					)
				)
			)
		)
	)
)

	AND ${filter}


	ORDER BY ${orderBy}
