mapper.delete:
DELETE FROM datasource
WHERE tenantid = #{tenantid}
AND id = #{id}

mapper.select:
SELECT *
FROM datasource
WHERE tenantid = #{tenantid}
AND id = #{id}

SELECT datasource.*
FROM datasource datasource
WHERE datasource.tenantid = #{tenantid}

	ORDER BY ${orderBy}

SELECT datasource.*
FROM datasource datasource
WHERE datasource.tenantid = #{tenantid}
AND datasource.name = #{name}
AND datasource.version = #{version}

	ORDER BY ${orderBy}
