mapper.delete:
DELETE FROM dependency
WHERE tenantid = #{tenantid}
AND id = #{id}

mapper.select:
SELECT *
FROM dependency dependency
WHERE dependency.tenantid = #{tenantid}

	ORDER BY ${orderBy}

SELECT *
FROM dependency
WHERE dependency.tenantid = #{tenantid}
AND dependency.id = #{id}

	ORDER BY ${orderBy}

SELECT *
FROM dependency
WHERE dependency.tenantid = #{tenantid}
AND dependency.name = #{name}

	ORDER BY ${orderBy}

SELECT dependencyId
FROM dependencymapping
WHERE tenantid = #{tenantid}
AND artifactId = #{artifactId}
AND artifactType = #{artifactType}

	ORDER BY ${orderBy}

SELECT *
FROM dependency dependency
WHERE dependency.tenantid = #{tenantid}
AND dependency.id IN

	#{item}


	ORDER BY ${orderBy}
