mapper.delete:
DELETE FROM dependencymapping
WHERE tenantid = #{tenantid}
AND id = #{id}

mapper.select:
SELECT dependencymapping.*
FROM dependencymapping,
	 dependency
WHERE dependencymapping.tenantid = #{tenantid}
AND dependency.tenantid = dependencymapping.tenantid
AND dependencymapping.artifactId = #{artifactId}
AND dependencymapping.artifactType = #{artifactType}

	ORDER BY ${orderBy}

SELECT *
FROM dependencymapping dependencymapping
WHERE dependencymapping.tenantid = #{tenantid}

	ORDER BY ${orderBy}

SELECT *
FROM dependencymapping dependencymapping
WHERE dependencymapping.tenantid = #{tenantid}
AND dependencymapping.id = #{id}

	ORDER BY ${orderBy}

SELECT *
FROM dependencymapping dependencymapping
WHERE dependencymapping.tenantid = #{tenantid}
AND dependencymapping.dependencyId = #{dependencyId}

	ORDER BY ${orderBy}
