mapper.update:
UPDATE p_metadata_def
name=NULL, name=#{name}, displayName=NULL, displayName=#{displayName}, description=NULL, description=#{description},
WHERE tenantid = #{tenantid} AND id = #{id}

mapper.delete:
DELETE FROM p_metadata_def
WHERE tenantid = #{tenantid}
AND id = #{id}

mapper.select:
SELECT *
FROM p_metadata_def pmdd
WHERE pmdd.tenantid = #{tenantid}
AND pmdd.id = #{id}

	ORDER BY ${orderBy}

SELECT *
FROM p_metadata_def pmdd
WHERE pmdd.tenantid = #{tenantid}

	ORDER BY ${orderBy}

SELECT *
FROM p_metadata_def pmdd
WHERE pmdd.tenantid = #{tenantid}

	${whereClause}


	ORDER BY ${orderBy}

SELECT COUNT(id)
FROM p_metadata_def pmdd
WHERE pmdd.tenantid = #{tenantid}

SELECT *
FROM p_metadata_def pmdd
WHERE pmdd.tenantid = #{tenantid}
AND pmdd.name = #{name}

	ORDER BY ${orderBy}
