mapper.update:
UPDATE role
name=NULL, name=#{name}, displayName=NULL, displayName=#{displayName}, description=NULL, description=#{description}, iconName=NULL, iconName=#{iconName}, iconPath=NULL, iconPath=#{iconPath}, createdBy=#{createdBy}, creationDate=#{creationDate}, lastUpdate=#{lastUpdate},
WHERE tenantid = #{tenantid} AND id = #{id}

mapper.delete:
DELETE FROM role
WHERE tenantid = #{tenantid}
AND id = #{id}

mapper.select:
SELECT *
FROM role
WHERE tenantid = #{tenantid}
AND id = #{id}

SELECT *
FROM role r
WHERE r.tenantid = #{tenantid}
AND r.id IN

	#{item}


	ORDER BY ${orderBy}

SELECT *
FROM role r
WHERE r.tenantid = #{tenantid}
AND r.name = #{name}

	ORDER BY ${orderBy}

SELECT *
FROM role r
WHERE r.tenantid = #{tenantid}

	ORDER BY ${orderBy}

SELECT *
FROM role r
WHERE r.tenantid = #{tenantid}

	AND ${filter}


	ORDER BY ${orderBy}

SELECT COUNT(*)
FROM role r
WHERE r.tenantid = #{tenantid}

	AND ${filter}
