mapper.update:
UPDATE group_
name=NULL, name=#{name}, displayName=NULL, displayName=#{displayName}, description=NULL, description=#{description}, parentPath=NULL, parentPath=#{parentPath}, iconName=NULL, iconName=#{iconName}, iconPath=NULL, iconPath=#{iconPath}, createdBy=NULL, createdBy=#{createdBy}, creationDate=#{creationDate}, lastUpdate=#{lastUpdate},
WHERE tenantid = #{tenantid} AND id = #{id}

mapper.delete:
DELETE FROM group_
WHERE tenantid = #{tenantid}
AND id = #{id}

mapper.select:
SELECT *
FROM group_ g
WHERE g.tenantid = #{tenantid}
AND g.id = #{id}

	ORDER BY ${orderBy}

SELECT *
FROM group_ g
WHERE g.tenantid = #{tenantid}
AND g.id IN

	#{item}


	ORDER BY ${orderBy}

SELECT *
FROM group_ g
WHERE g.tenantid = #{tenantid}
AND g.name = #{name}
AND g.parentPath IS NULL

SELECT *
FROM group_ g
WHERE g.tenantid = #{tenantid}
AND g.name = #{name}
AND g.parentPath = #{parentPath}

SELECT *
FROM group_ g
WHERE g.tenantid = #{tenantid}
AND g.name = #{name}

	ORDER BY ${orderBy}

SELECT *
FROM group_ g
WHERE g.tenantid = #{tenantid}

	ORDER BY ${orderBy}

SELECT *
FROM group_ g
WHERE g.tenantid = #{tenantid}

	AND ${filter}


	ORDER BY ${orderBy}

SELECT COUNT(*)
FROM group_ g
WHERE g.tenantid = #{tenantid}

	AND ${filter}

SELECT *
FROM group_ g
WHERE g.tenantid = #{tenantid}
AND g.parentPath = #{groupPath}

	ORDER BY ${orderBy}

SELECT COUNT(id)
FROM group_ g
WHERE g.tenantid = #{tenantid}

	AND parentPath IS NULL


	AND parentPath = #{parentPath}
