mapper.update:
UPDATE p_metadata_val
userName=#{userName}, metadataName=#{metadataId}, value=NULL, value=#{value},
WHERE tenantid = #{tenantid} AND id = #{id}

mapper.delete:
DELETE FROM p_metadata_val
WHERE tenantid = #{tenantid}
AND id = #{id}

mapper.select:
SELECT *
FROM p_metadata_val pmdv
WHERE pmdv.tenantid = #{tenantid}
AND pmdv.id = #{id}

	ORDER BY ${orderBy}

SELECT *
FROM p_metadata_val pmdv
WHERE pmdv.tenantid = #{tenantid}

	${whereClause}


	ORDER BY ${orderBy}

SELECT *
FROM p_metadata_val
WHERE pmdv.tenantid = #{tenantid}

	ORDER BY ${orderBy}
