mapper.update:
UPDATE profile
name = NULL, name = #{name}, description = NULL, description = #{description}, iconPath = NULL, iconPath = #{iconPath},
WHERE tenantId = #{tenantid} AND id = #{id}

mapper.delete:
DELETE FROM profile
WHERE tenantId = #{tenantid}
AND id = #{id}

mapper.select:
SELECT *
FROM profile
WHERE tenantId = #{tenantid}
AND id = #{id}

SELECT *
FROM profile
WHERE tenantId = #{tenantid}
AND name = #{name}

SELECT COUNT(*)
FROM profile
WHERE tenantId = #{tenantid}

SELECT *
FROM profile p
WHERE p.tenantId = #{tenantid}

	ORDER BY ${orderBy}
