mapper.delete:
DELETE FROM job_param
WHERE TENANTID = #{tenantid}
AND ID = #{id}

mapper.select:
SELECT jp.*
FROM job_param jp
WHERE jp.tenantid = #{tenantid}
AND jp.id = #{id}

	ORDER BY ${orderBy}

SELECT jp.*
FROM job_param jp
WHERE jp.tenantid = #{tenantid}
AND jp.jobDescriptorId = #{jobDescriptorId}

	ORDER BY ${orderBy}
