mapper.select:
SELECT employee.*
FROM employee
WHERE id = #{id}
AND tenantid = #{tenantid}

SELECT employee.*
FROM employee
WHERE tenantid = #{tenantid}
AND age = #{age}

mapper.delete:
DELETE FROM employee
WHERE tenantid = #{tenantid}
AND id = #{id}

mapper.update:
UPDATE employee
name=NULL, name=#{name}, age=#{age}, laptopId=#{laptopId}, archivedate=#{archiveDate},
WHERE TENANTID = #{tenantid} AND id = #{id}
