mapper.select:

SELECT saemployee.*
FROM saemployee
WHERE tenantid = #{tenantid}

	AND archiveDate < #{time}


SELECT saemployee.*
FROM saemployee
WHERE tenantid = #{tenantid}


SELECT saemployee.*
FROM saemployee
WHERE id = #{id}
AND tenantid = #{tenantid}

mapper.delete:
		DELETE FROM saemployee
		WHERE tenantid = #{tenantid}
      AND id = #{id}
